var vinouEnquiry={wines:document.querySelectorAll('input[name*="[quantity]"]'),init:function(){this.bindEvents()},calculateSum:function(){for(var e=this,t=0,n=0;n<e.wines.length;n++)if(e.wines[n].value>0){var i=e.wines[n].id.replace("quantity","price"),u=document.getElementById(i);t+=parseFloat(u.value)*parseInt(e.wines[n].value)}var a=t.toFixed(2)/1.19,o=parseFloat(t.toFixed(2))-parseFloat(a.toFixed(2));document.getElementById("tx_vinou_enquiry[net]").value=a.toFixed(2),document.getElementById("tx_vinou_enquiry[tax]").value=o.toFixed(2),document.getElementById("tx_vinou_enquiry[gross]").value=t.toFixed(2)},bindEvents:function(){for(var e=this,t=0;t<e.wines.length;t++)e.wines[t].addEventListener("input",(function(){e.calculateSum()}))}};vinouEnquiry.init();